dunes-script-module.script:
function Handler($context, $inputs) { $vcHostname = $inputs.vcHostname $vcUsername = $inputs.vcUsername $vcPassword = $inputs.vcPassword $vmName = $inputs.vmName
Set-PowerCLIConfiguration -InvalidCertificateAction Ignore -Confirm:$false | Out-Null Connect-VIServer $vcHostname -User $vcUsername -Password $vcPassword | Out-Null
$vm = Get-VM -Name "$vmName" Write-Output $vm.PowerState.ToString() }
